import React, { useState, useEffect } from 'react';
import { format } from 'date-fns';
import { Link } from 'react-router-dom';
import ExpenseTracker from '../components/ExpenseTracker';
import { ThemeToggle } from '../components/ThemeToggle';
import { api } from '../lib/api';
import { saveDataToLocalStorage, loadDataFromLocalStorage } from '../lib/storage';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './Home.css';

export default function Home({ darkMode, setDarkMode }) {
  const [isLoading, setIsLoading] = useState(true);
  const [wallet, setWallet] = useState({ id: 1, balance: "5000" });
  const [expenses, setExpenses] = useState([]);

  // Load initial data
  useEffect(() => {
    const fetchData = async () => {
      try {
        // First try to load from API
        const walletData = await api.getWallet();
        const expensesData = await api.getExpenses();
        
        setWallet(walletData);
        setExpenses(expensesData);
        
        // Save to localStorage for offline use
        saveDataToLocalStorage({ wallet: walletData, expenses: expensesData });
      } catch (error) {
        console.error("Error loading data:", error);
        toast.error("Failed to load data");
        
        // Try to load from localStorage as fallback
        const localData = loadDataFromLocalStorage();
        if (localData) {
          setWallet(localData.wallet);
          setExpenses(localData.expenses);
        }
      } finally {
        setIsLoading(false);
      }
    };
    
    fetchData();
  }, []);

  // Add income handler
  const handleAddIncome = async (amount) => {
    try {
      const updatedWallet = await api.addIncome(amount);
      setWallet(updatedWallet);
      saveDataToLocalStorage({ wallet: updatedWallet, expenses });
      
      toast.success("Income added successfully");
    } catch (error) {
      console.error("Error adding income:", error);
      toast.error("Failed to add income");
    }
  };

  // Add expense handler
  const handleAddExpense = async (data) => {
    try {
      const newExpense = await api.addExpense(data);
      
      // Update expense list
      setExpenses(prev => [newExpense, ...prev]);
      
      // Get updated wallet balance
      const updatedWallet = await api.getWallet();
      setWallet(updatedWallet);
      
      // Save to localStorage
      saveDataToLocalStorage({ 
        wallet: updatedWallet, 
        expenses: [newExpense, ...expenses] 
      });
      
      toast.success("Expense added successfully");
    } catch (error) {
      console.error("Error adding expense:", error);
      toast.error(error.message || "Failed to add expense");
    }
  };

  // Update expense handler
  const handleUpdateExpense = async (id, data) => {
    try {
      const updatedExpense = await api.updateExpense(id, data);
      
      // Update expenses list
      setExpenses(prev => 
        prev.map(expense => 
          expense.id === id ? updatedExpense : expense
        )
      );
      
      // Get updated wallet balance
      const updatedWallet = await api.getWallet();
      setWallet(updatedWallet);
      
      // Save to localStorage
      saveDataToLocalStorage({ 
        wallet: updatedWallet, 
        expenses: expenses.map(expense => 
          expense.id === id ? updatedExpense : expense
        ) 
      });
      
      toast.success("Expense updated successfully");
    } catch (error) {
      console.error("Error updating expense:", error);
      toast.error(error.message || "Failed to update expense");
    }
  };

  // Delete expense handler
  const handleDeleteExpense = async (id) => {
    try {
      await api.deleteExpense(id);
      
      // Update expenses list
      setExpenses(prev => prev.filter(expense => expense.id !== id));
      
      // Get updated wallet balance
      const updatedWallet = await api.getWallet();
      setWallet(updatedWallet);
      
      // Save to localStorage
      saveDataToLocalStorage({ 
        wallet: updatedWallet, 
        expenses: expenses.filter(expense => expense.id !== id) 
      });
      
      toast.success("Expense deleted successfully");
    } catch (error) {
      console.error("Error deleting expense:", error);
      toast.error("Failed to delete expense");
    }
  };

  // Get current date
  const currentDate = new Date();
  const formattedDate = format(currentDate, 'EEEE, MMMM d, yyyy • h:mm a');

  return (
    <div className="home-container">
      {isLoading ? (
        <div className="loading-container">
          <p>Loading...</p>
        </div>
      ) : (
        <>
          {/* Main Header */}
          <header className="main-header">
            <div className="header-content">
              <div>
                <h1 className="app-title">Expense Tracker</h1>
                <p className="app-subtitle">Manage your finances effectively</p>
              </div>
              <div className="header-actions">
                <nav className="desktop-nav">
                  <Link to="/reports" className="nav-link">Reports</Link>
                  <Link to="/budgets" className="nav-link">Budgets</Link>
                  <Link to="/bills" className="nav-link">Bills</Link>
                  <div className="theme-toggle-container">
                    <ThemeToggle darkMode={darkMode} setDarkMode={setDarkMode} />
                  </div>
                </nav>
                <button 
                  onClick={() => document.getElementById('add-expense-btn').click()}
                  className="header-btn add-expense-btn"
                >
                  + Add Expense
                </button>
                <button 
                  onClick={() => document.getElementById('add-income-btn').click()}
                  className="header-btn add-income-btn"
                >
                  + Add Income
                </button>
              </div>
            </div>
            
            {/* Date display */}
            <div className="date-display">
              <span>{formattedDate}</span>
            </div>
          </header>
          
          <ExpenseTracker
            wallet={wallet}
            expenses={expenses}
            onAddIncome={handleAddIncome}
            onAddExpense={handleAddExpense}
            onUpdateExpense={handleUpdateExpense}
            onDeleteExpense={handleDeleteExpense}
          />
          
          <ToastContainer 
            position="bottom-right"
            autoClose={3000}
            hideProgressBar={false}
            newestOnTop
            closeOnClick
            rtl={false}
            pauseOnFocusLoss
            draggable
            pauseOnHover
            theme={darkMode ? "dark" : "light"}
          />
        </>
      )}
    </div>
  );
}
