import React, { useState, useEffect } from 'react';
import { format } from 'date-fns';
import { FiCalendar, FiPlus, FiRefreshCw, FiEdit2, FiTrash2 } from 'react-icons/fi';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { loadDataFromLocalStorage, saveDataToLocalStorage, calculateCategoryTotals } from '../lib/storage';
import AddExpenseModal from '../components/modals/AddExpenseModal';
import AddIncomeModal from '../components/modals/AddIncomeModal';
import EditExpenseModal from '../components/modals/EditExpenseModal';
import { 
  BarChart, Bar, PieChart, Pie, Cell, 
  ResponsiveContainer, LineChart, Line,
  Tooltip, Legend
} from 'recharts';
import '../App.css';

export default function Home() {
  const [isLoading, setIsLoading] = useState(true);
  const [wallet, setWallet] = useState({ id: 1, balance: "2000" });
  const [expenses, setExpenses] = useState([]);
  const [showAddExpenseModal, setShowAddExpenseModal] = useState(false);
  const [showAddIncomeModal, setShowAddIncomeModal] = useState(false);
  const [showEditExpenseModal, setShowEditExpenseModal] = useState(false);
  const [currentExpense, setCurrentExpense] = useState(null);
  
  // Budget settings
  const monthlyBudget = 8000;
  const savingsGoal = 15000;

  // Calculate category totals
  const categoryTotals = calculateCategoryTotals(expenses);
  
  // Prepare chart data
  const prepareCategoryData = () => {
    const categories = Object.keys(categoryTotals);
    return categories.map(category => ({
      name: category.charAt(0).toUpperCase() + category.slice(1),
      value: categoryTotals[category],
      color: getCategoryColor(category)
    })).filter(item => item.value > 0).sort((a, b) => b.value - a.value);
  };
  
  const getCategoryColor = (category) => {
    const colorMap = {
      food: '#ac5ddf',
      entertainment: '#ffc32b',
      travel: '#4bd268',
      study: '#49a7ff',
      utilities: '#ff5c81',
      electronics: '#3ee0cf',
      other: '#94a3b8'
    };
    return colorMap[category.toLowerCase()] || '#94a3b8';
  };
  
  // Weekly spending data
  const generateWeeklyData = () => {
    const days = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];
    const data = days.map(day => {
      const dayExpenses = expenses.filter(expense => {
        const expenseDate = new Date(expense.date);
        const dayOfWeek = expenseDate.getDay(); // 0 = Sunday, 1 = Monday, etc.
        const dayMap = {
          'Mon': 1, 'Tue': 2, 'Wed': 3, 'Thu': 4, 'Fri': 5, 'Sat': 6, 'Sun': 0
        };
        return dayMap[day] === dayOfWeek;
      });
      const totalForDay = dayExpenses.reduce((sum, expense) => sum + parseFloat(expense.amount), 0);
      return {
        day,
        amount: totalForDay
      };
    });
    return data;
  };

  // Get top expense categories for pie chart
  const getTopCategories = () => {
    const categoryData = prepareCategoryData();
    // Take top 3 categories and group the rest as "Other"
    if (categoryData.length <= 3) return categoryData;
    
    const top3 = categoryData.slice(0, 3);
    const otherTotal = categoryData.slice(3).reduce((sum, item) => sum + item.value, 0);
    
    if (otherTotal > 0) {
      top3.push({ name: 'Other', value: otherTotal, color: '#94a3b8' });
    }
    
    return top3;
  };

  // Load initial data
  useEffect(() => {
    const fetchData = async () => {
      try {
        const localData = loadDataFromLocalStorage();
        if (localData) {
          setWallet(localData.wallet);
          setExpenses(localData.expenses);
        }
      } catch (error) {
        console.error("Error loading data:", error);
      } finally {
        setIsLoading(false);
      }
    };
    
    fetchData();
  }, []);

  const handleAddIncome = (amount) => {
    try {
      const newBalance = parseFloat(wallet.balance) + amount;
      const updatedWallet = { ...wallet, balance: newBalance.toString() };
      setWallet(updatedWallet);
      
      saveDataToLocalStorage({ wallet: updatedWallet, expenses });
      toast.success("Income added successfully");
    } catch (error) {
      toast.error("Failed to add income");
    }
  };

  const handleAddExpense = (data) => {
    try {
      const newExpense = {
        id: Date.now(),
        ...data,
        amount: parseFloat(data.amount),
        created_at: new Date()
      };
      
      const newBalance = parseFloat(wallet.balance) - parseFloat(data.amount);
      if (newBalance < 0) {
        toast.error("Insufficient funds in wallet");
        return;
      }
      
      const updatedWallet = { ...wallet, balance: newBalance.toString() };
      setWallet(updatedWallet);
      
      setExpenses(prev => [newExpense, ...prev]);
      
      saveDataToLocalStorage({ 
        wallet: updatedWallet, 
        expenses: [newExpense, ...expenses] 
      });
      
      toast.success("Expense added successfully");
    } catch (error) {
      toast.error("Failed to add expense");
    }
  };

  const handleEditExpense = (expense) => {
    setCurrentExpense(expense);
    setShowEditExpenseModal(true);
  };

  const handleUpdateExpense = (id, data) => {
    try {
      const expenseToUpdate = expenses.find(e => e.id === id);
      if (!expenseToUpdate) return;
      
      const oldAmount = parseFloat(expenseToUpdate.amount);
      const newAmount = parseFloat(data.amount || oldAmount);
      const amountDiff = oldAmount - newAmount;
      
      const updatedExpense = { ...expenseToUpdate, ...data };
      
      setExpenses(prev => 
        prev.map(expense => expense.id === id ? updatedExpense : expense)
      );
      
      if (amountDiff !== 0) {
        const newBalance = parseFloat(wallet.balance) + amountDiff;
        if (newBalance < 0) {
          toast.error("Insufficient funds in wallet");
          return;
        }
        
        const updatedWallet = { ...wallet, balance: newBalance.toString() };
        setWallet(updatedWallet);
        
        saveDataToLocalStorage({ 
          wallet: updatedWallet, 
          expenses: expenses.map(expense => 
            expense.id === id ? updatedExpense : expense
          ) 
        });
      }
      
      toast.success("Expense updated successfully");
    } catch (error) {
      toast.error("Failed to update expense");
    }
  };

  const handleDeleteExpense = (id) => {
    try {
      const expenseToDelete = expenses.find(e => e.id === id);
      if (!expenseToDelete) return;
      
      setExpenses(prev => prev.filter(expense => expense.id !== id));
      
      const newBalance = parseFloat(wallet.balance) + parseFloat(expenseToDelete.amount);
      const updatedWallet = { ...wallet, balance: newBalance.toString() };
      setWallet(updatedWallet);
      
      saveDataToLocalStorage({ 
        wallet: updatedWallet, 
        expenses: expenses.filter(expense => expense.id !== id) 
      });
      
      toast.success("Expense deleted successfully");
    } catch (error) {
      toast.error("Failed to delete expense");
    }
  };

  // Calculate total expenses
  const totalExpenses = expenses.reduce((sum, expense) => sum + parseFloat(expense.amount), 0);

  // Format current date
  const currentDate = format(new Date(), 'EEEE, MMMM d, yyyy â€¢ h:mm a');

  if (isLoading) {
    return (
      <div className="loading-container">
        <p>Loading...</p>
      </div>
    );
  }

  const budgetPercentage = Math.min(100, Math.round((totalExpenses / monthlyBudget) * 100));
  const savingsPercentage = Math.min(100, Math.round((parseFloat(wallet.balance) / savingsGoal) * 100));

  // Get top category if expenses exist
  const topCategory = expenses.length > 0 
    ? Object.entries(categoryTotals).reduce((a, b) => a[1] > b[1] ? a : b)[0] 
    : 'Travel';

  // Weekly data
  const weeklyData = generateWeeklyData();
  
  // Top category data for pie chart
  const topCategoriesData = getTopCategories();

  return (
    <div className="app-container">
      {/* Header separator */}
      <div className="app-header-separator"></div>
      
      {/* Header */}
      <div className="header-container">
        <h1 className="header-title">Expense Tracker</h1>
        
        <div className="header-date">
          <FiCalendar />
          <span>{currentDate}</span>
        </div>
        
        <div className="header-actions">
          <div className="main-nav">
            <a href="#reports">Reports</a>
            <a href="#budgets">Budgets</a>
            <a href="#bills">Bills</a>
          </div>
          
          <button className="btn btn-primary" onClick={() => setShowAddExpenseModal(true)}>
            + Add Expense
          </button>
          
          <button className="btn btn-outline" onClick={() => setShowAddIncomeModal(true)}>
            + Add Income
          </button>
        </div>
      </div>
      
      {/* Main Dashboard Grid */}
      <div className="dashboard-grid">
        {/* Wallet Card */}
        <div className="card">
          <div className="card-header">
            <h2 className="card-title">Wallet Balance</h2>
            <button className="btn-icon">
              <FiRefreshCw size={16} />
            </button>
          </div>
          <div className="card-body">
            <div className="wallet-amount">${parseFloat(wallet.balance).toLocaleString('en-US', {minimumFractionDigits: 2})}</div>
            
            {/* Weekly Spending Chart */}
            <div style={{ height: "70px", marginBottom: "15px" }}>
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={weeklyData} margin={{ top: 5, right: 5, left: 5, bottom: 5 }}>
                  <Bar dataKey="amount" fill="#10b981" radius={[4, 4, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            </div>
            
            <div className="progress-bar">
              <div 
                className="progress-value" 
                style={{ 
                  width: '60%',
                  backgroundColor: 'var(--success)'
                }}
              ></div>
            </div>
            
            <div className="stats-grid">
              <div>
                <div className="stat-label">Available</div>
                <div className="stat-value">${parseFloat(wallet.balance).toLocaleString('en-US', {minimumFractionDigits: 2})}</div>
                <div className="stat-label">60%</div>
              </div>
              
              <div>
                <div className="stat-label">Spent</div>
                <div className="stat-value">${totalExpenses.toLocaleString('en-US', {minimumFractionDigits: 2})}</div>
              </div>
            </div>
            
            <div className="savings-goal">
              <div className="savings-header">
                <div className="stat-label">Savings Goal</div>
                <div className="stat-value">${savingsGoal.toLocaleString('en-US')}</div>
              </div>
              
              <div className="progress-bar">
                <div 
                  className="progress-value" 
                  style={{ 
                    width: `${savingsPercentage}%`,
                    backgroundColor: 'var(--primary)'
                  }}
                ></div>
              </div>
              
              <div className="savings-meta">
                <span>{savingsPercentage}% completed</span>
                <span>${parseFloat(wallet.balance).toLocaleString('en-US')} / ${savingsGoal.toLocaleString('en-US')}</span>
              </div>
            </div>
            
            <button className="add-income-btn" onClick={() => setShowAddIncomeModal(true)}>
              <FiRefreshCw size={16} />
              Add Income
            </button>
          </div>
        </div>
        
        {/* Expense Card */}
        <div className="card">
          <div className="card-header">
            <h2 className="card-title">Expenses</h2>
            <button className="btn-icon" onClick={() => setShowAddExpenseModal(true)}>
              <FiPlus size={16} />
            </button>
          </div>
          <div className="card-body">
            <div className="expense-amount">${totalExpenses.toLocaleString('en-US', {minimumFractionDigits: 2})}</div>
            
            {/* Category Breakdown */}
            <div style={{ height: "70px", marginBottom: "15px" }}>
              <ResponsiveContainer width="100%" height="100%">
                <BarChart 
                  data={prepareCategoryData().slice(0, 5)} 
                  layout="vertical" 
                  margin={{ top: 5, right: 5, left: 5, bottom: 5 }}
                >
                  <Bar dataKey="value" radius={[0, 4, 4, 0]}>
                    {prepareCategoryData().slice(0, 5).map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={entry.color} />
                    ))}
                  </Bar>
                </BarChart>
              </ResponsiveContainer>
            </div>
            
            <div className="progress-bar">
              <div 
                className="progress-value" 
                style={{ 
                  width: `${budgetPercentage}%`,
                  backgroundColor: 'var(--danger)'
                }}
              ></div>
            </div>
            
            <div className="stats-grid">
              <div>
                <div className="stat-label">Top Category</div>
                <div className="stat-value">{topCategory.charAt(0).toUpperCase() + topCategory.slice(1)}</div>
              </div>
              
              <div>
                <div className="stat-label">Avg. Expense</div>
                <div className="stat-value">
                  ${expenses.length ? (totalExpenses / expenses.length).toFixed(2) : '0.00'}
                </div>
              </div>
            </div>
            
            <button className="add-expense-btn" onClick={() => setShowAddExpenseModal(true)}>
              <FiPlus size={16} />
              Add Expense
            </button>
          </div>
        </div>
        
        {/* Budget Card */}
        <div className="card">
          <div className="card-header">
            <h2 className="card-title">Budget Overview</h2>
            <span className="stat-label">{budgetPercentage}% used</span>
          </div>
          <div className="card-body">
            <div className="budget-chart">
              <ResponsiveContainer width="100%" height="100%">
                <PieChart>
                  <Pie
                    data={[
                      { name: 'Used', value: totalExpenses },
                      { name: 'Remaining', value: Math.max(0, monthlyBudget - totalExpenses) }
                    ]}
                    cx="50%"
                    cy="50%"
                    innerRadius={50}
                    outerRadius={70}
                    startAngle={90}
                    endAngle={-270}
                    paddingAngle={0}
                    dataKey="value"
                  >
                    <Cell fill="#7c3aed" />
                    <Cell fill="#e2e8f0" />
                  </Pie>
                </PieChart>
              </ResponsiveContainer>
            </div>
            
            <div className="stats-grid">
              <div>
                <div className="stat-label">Monthly Budget</div>
                <div className="stat-value">${monthlyBudget.toLocaleString('en-US')}</div>
              </div>
              
              <div>
                <div className="stat-label">Spent so far</div>
                <div className="stat-value">${totalExpenses.toLocaleString('en-US')}</div>
              </div>
            </div>
            
            <div className="budget-message">
              <div className="budget-status">
                {totalExpenses <= monthlyBudget 
                  ? "You're under budget. Good job!" 
                  : "You've exceeded your budget."}
              </div>
              <div className="budget-remaining">
                ${Math.abs(monthlyBudget - totalExpenses).toLocaleString('en-US')} 
                {totalExpenses <= monthlyBudget ? " remaining" : " over budget"}
              </div>
            </div>
            
            <div className="budget-actions">
              <button className="btn btn-outline">Reports</button>
              <button className="btn btn-outline">Budgets</button>
              <button className="btn btn-outline">Bills</button>
            </div>
          </div>
        </div>
      </div>

      {/* Transactions Grid */}
      <div className="transactions-grid">
        <div className="card">
          <div className="card-header">
            <h2 className="card-title">Recent Transactions</h2>
          </div>
          <div className="card-body">
            {expenses.length > 0 ? (
              <div className="transaction-list">
                {expenses.slice(0, 5).map(expense => (
                  <div key={expense.id} className="transaction-item">
                    <div>
                      <div>{expense.title}</div>
                      <div className="stat-label">{new Date(expense.date).toLocaleDateString()}</div>
                    </div>
                    <div>${parseFloat(expense.amount).toFixed(2)}</div>
                    <div>
                      <button className="btn-icon" onClick={() => handleEditExpense(expense)}>
                        <FiEdit2 size={14} />
                      </button>
                      <button className="btn-icon" onClick={() => handleDeleteExpense(expense.id)}>
                        <FiTrash2 size={14} />
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="empty-state">
                <p>No transactions yet.</p>
                <div className="add-first" onClick={() => setShowAddExpenseModal(true)}>
                  Add your first expense
                </div>
              </div>
            )}
          </div>
        </div>
        
        <div className="card">
          <div className="card-header">
            <h2 className="card-title">Top Expenses</h2>
          </div>
          <div className="card-body">
            {expenses.length > 0 ? (
              <div>
                <ResponsiveContainer width="100%" height={250}>
                  <PieChart>
                    <Pie
                      data={topCategoriesData}
                      cx="50%"
                      cy="50%"
                      labelLine={false}
                      outerRadius={80}
                      fill="#8884d8"
                      dataKey="value"
                    >
                      {topCategoriesData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={entry.color} />
                      ))}
                    </Pie>
                    <Tooltip 
                      formatter={(value) => [`$${value.toFixed(2)}`, 'Amount']}
                      labelFormatter={(name) => `Category: ${name}`}
                    />
                    <Legend />
                  </PieChart>
                </ResponsiveContainer>
              </div>
            ) : (
              <div className="empty-state">
                <p>No expense data available</p>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Modals */}
      {showAddExpenseModal && (
        <AddExpenseModal
          onClose={() => setShowAddExpenseModal(false)}
          onAddExpense={(data) => {
            handleAddExpense(data);
            setShowAddExpenseModal(false);
          }}
        />
      )}

      {showAddIncomeModal && (
        <AddIncomeModal
          onClose={() => setShowAddIncomeModal(false)}
          onAddIncome={(amount) => {
            handleAddIncome(amount);
            setShowAddIncomeModal(false);
          }}
        />
      )}

      {showEditExpenseModal && currentExpense && (
        <EditExpenseModal
          expense={currentExpense}
          onClose={() => {
            setShowEditExpenseModal(false);
            setCurrentExpense(null);
          }}
          onUpdateExpense={(data) => {
            handleUpdateExpense(currentExpense.id, data);
            setShowEditExpenseModal(false);
            setCurrentExpense(null);
          }}
        />
      )}

      <ToastContainer position="bottom-right" theme="dark" />
    </div>
  );
}
