import React from 'react';
import { format } from 'date-fns';
import { FiEdit2, FiTrash2 } from 'react-icons/fi';
import { CATEGORY_COLORS, CATEGORY_ICONS } from '../lib/types';
import './TransactionList.css';

export default function TransactionList({ expenses, onEdit, onDelete }) {
  // Import all category icons
  const icons = {
    food: require('react-icons/fi').FiShoppingBag,
    entertainment: require('react-icons/fi').FiFilm,
    travel: require('react-icons/fi').FiMapPin,
    study: require('react-icons/fi').FiBook,
    utensils: require('react-icons/fi').FiCoffee,
    electronics: require('react-icons/fi').FiSmartphone
  };

  return (
    <div className="transaction-list">
      {expenses.map((expense) => {
        const IconComponent = icons[expense.category] || icons.food;
        
        return (
          <div key={expense.id} className="transaction-item">
            <div className="transaction-icon" style={{ backgroundColor: `${CATEGORY_COLORS[expense.category]}20`, color: CATEGORY_COLORS[expense.category] }}>
              <IconComponent size={16} />
            </div>
            
            <div className="transaction-details">
              <div className="transaction-title-row">
                <h3 className="transaction-title">{expense.title}</h3>
                <span className="transaction-amount">â‚¹{Number(expense.amount).toLocaleString()}</span>
              </div>
              
              <div className="transaction-meta">
                <span className="transaction-category">{expense.category.charAt(0).toUpperCase() + expense.category.slice(1)}</span>
                <span className="transaction-date">
                  {format(new Date(expense.date), 'MMM d, yyyy')}
                </span>
              </div>
            </div>
            
            <div className="transaction-actions">
              <button 
                className="action-btn edit-btn"
                onClick={() => onEdit(expense)}
                aria-label={`Edit ${expense.title}`}
              >
                <FiEdit2 size={16} />
              </button>
              
              <button 
                className="action-btn delete-btn"
                onClick={() => onDelete(expense.id)}
                aria-label={`Delete ${expense.title}`}
              >
                <FiTrash2 size={16} />
              </button>
            </div>
          </div>
        );
      })}
    </div>
  );
}
