import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { format } from 'date-fns';
import { FiArrowLeft, FiPlus, FiEdit2, FiTrash2, FiCheck, FiCalendar } from 'react-icons/fi';
import { ToastContainer, toast } from 'react-toastify';
import AddBillModal from '../components/modals/AddBillModal';
import EditBillModal from '../components/modals/EditBillModal';

export default function Bills({ darkMode }) {
  const [bills, setBills] = useState([]);
  const [showAddBillModal, setShowAddBillModal] = useState(false);
  const [showEditBillModal, setShowEditBillModal] = useState(false);
  const [currentBill, setCurrentBill] = useState(null);
  
  // Load bills from localStorage
  useEffect(() => {
    const loadBills = () => {
      try {
        const savedBills = localStorage.getItem('expense_tracker_bills');
        if (savedBills) {
          setBills(JSON.parse(savedBills));
        }
      } catch (error) {
        console.error("Error loading bills:", error);
      }
    };
    
    loadBills();
  }, []);
  
  // Save bills to localStorage
  const saveBills = (updatedBills) => {
    try {
      localStorage.setItem('expense_tracker_bills', JSON.stringify(updatedBills));
    } catch (error) {
      console.error("Error saving bills:", error);
    }
  };
  
  // Add new bill
  const handleAddBill = (bill) => {
    try {
      const newBill = {
        id: Date.now(),
        ...bill,
        status: 'pending'
      };
      
      const updatedBills = [newBill, ...bills];
      setBills(updatedBills);
      saveBills(updatedBills);
      
      toast.success("Bill added successfully");
    } catch (error) {
      toast.error("Failed to add bill");
    }
  };
  
  // Edit bill
  const handleEditBill = (bill) => {
    setCurrentBill(bill);
    setShowEditBillModal(true);
  };
  
  // Update bill
  const handleUpdateBill = (id, updates) => {
    try {
      const updatedBills = bills.map(bill => 
        bill.id === id ? { ...bill, ...updates } : bill
      );
      
      setBills(updatedBills);
      saveBills(updatedBills);
      
      toast.success("Bill updated successfully");
    } catch (error) {
      toast.error("Failed to update bill");
    }
  };
  
  // Delete bill
  const handleDeleteBill = (id) => {
    try {
      const updatedBills = bills.filter(bill => bill.id !== id);
      
      setBills(updatedBills);
      saveBills(updatedBills);
      
      toast.success("Bill deleted successfully");
    } catch (error) {
      toast.error("Failed to delete bill");
    }
  };
  
  // Mark bill as paid
  const handleMarkAsPaid = (id) => {
    try {
      const updatedBills = bills.map(bill => 
        bill.id === id 
          ? { 
              ...bill, 
              status: 'paid', 
              paidDate: new Date().toISOString().slice(0, 10) 
            } 
          : bill
      );
      
      setBills(updatedBills);
      saveBills(updatedBills);
      
      toast.success("Bill marked as paid");
    } catch (error) {
      toast.error("Failed to update bill");
    }
  };
  
  // Group bills by status
  const pendingBills = bills.filter(bill => bill.status === 'pending');
  const paidBills = bills.filter(bill => bill.status === 'paid');
  
  // Calculate total bills amount
  const totalPendingAmount = pendingBills.reduce((sum, bill) => sum + parseFloat(bill.amount), 0);
  
  // Sort bills by due date (closest first)
  const sortedPendingBills = [...pendingBills].sort((a, b) => {
    return new Date(a.dueDate) - new Date(b.dueDate);
  });
  
  // Calculate upcoming bills (due in the next 7 days)
  const today = new Date();
  const nextWeek = new Date();
  nextWeek.setDate(today.getDate() + 7);
  
  const upcomingBills = sortedPendingBills.filter(bill => {
    const dueDate = new Date(bill.dueDate);
    return dueDate >= today && dueDate <= nextWeek;
  });

  return (
    <div className="page-container">
      <Link to="/" className="back-button">
        <FiArrowLeft />
        Back to Dashboard
      </Link>
      
      <div className="page-header">
        <h1 className="page-title">Bills & Payments</h1>
        <p className="page-description">Manage your recurring bills and track payment status</p>
      </div>
      
      <div className="bills-summary">
        <div className="bills-overview">
          <div className="bills-card">
            <h3>Pending Bills</h3>
            <div className="bills-amount">${totalPendingAmount.toLocaleString()}</div>
            <div className="bills-count">{pendingBills.length} bills pending</div>
          </div>
          
          <div className="bills-actions">
            <button 
              className="btn btn-primary"
              onClick={() => setShowAddBillModal(true)}
            >
              <FiPlus />
              Add New Bill
            </button>
          </div>
        </div>
        
        {/* Upcoming Bills */}
        <div className="upcoming-bills">
          <h3>Upcoming Bills</h3>
          {upcomingBills.length > 0 ? (
            <div className="upcoming-bills-list">
              {upcomingBills.map(bill => (
                <div className="upcoming-bill-item" key={bill.id}>
                  <div className="bill-info">
                    <div className="bill-name">{bill.name}</div>
                    <div className="bill-amount">${parseFloat(bill.amount).toLocaleString()}</div>
                  </div>
                  <div className="bill-meta">
                    <div className="bill-date">
                      <FiCalendar className="bill-icon" />
                      Due: {format(new Date(bill.dueDate), 'MMM dd, yyyy')}
                    </div>
                    <button 
                      className="btn-mark-paid" 
                      onClick={() => handleMarkAsPaid(bill.id)}
                    >
                      <FiCheck />
                      Mark as Paid
                    </button>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="no-bills">
              <p>No upcoming bills due in the next 7 days</p>
            </div>
          )}
        </div>
        
        {/* All Bills Table */}
        <div className="all-bills">
          <h3>All Bills</h3>
          {bills.length > 0 ? (
            <div className="bills-table-container">
              <table className="bills-table">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Amount</th>
                    <th>Due Date</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {bills.map(bill => (
                    <tr key={bill.id} className={bill.status === 'paid' ? 'bill-paid' : ''}>
                      <td>{bill.name}</td>
                      <td>${parseFloat(bill.amount).toLocaleString()}</td>
                      <td>{format(new Date(bill.dueDate), 'MMM dd, yyyy')}</td>
                      <td>
                        <span className={`status-badge status-${bill.status}`}>
                          {bill.status === 'paid' ? 'Paid' : 'Pending'}
                        </span>
                        {bill.status === 'paid' && bill.paidDate && (
                          <div className="paid-date">
                            {format(new Date(bill.paidDate), 'MMM dd, yyyy')}
                          </div>
                        )}
                      </td>
                      <td>
                        <div className="bill-actions">
                          {bill.status === 'pending' && (
                            <button 
                              className="action-button mark-paid-button" 
                              onClick={() => handleMarkAsPaid(bill.id)}
                              aria-label="Mark as paid"
                            >
                              <FiCheck />
                            </button>
                          )}
                          <button 
                            className="action-button edit-button" 
                            onClick={() => handleEditBill(bill)}
                            aria-label="Edit bill"
                          >
                            <FiEdit2 />
                          </button>
                          <button 
                            className="action-button delete-button" 
                            onClick={() => handleDeleteBill(bill.id)}
                            aria-label="Delete bill"
                          >
                            <FiTrash2 />
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="no-bills">
              <p>No bills added yet</p>
              <button 
                className="btn btn-outline" 
                onClick={() => setShowAddBillModal(true)}
              >
                <FiPlus />
                Add Your First Bill
              </button>
            </div>
          )}
        </div>
      </div>
      
      {/* Add Bill Modal */}
      {showAddBillModal && (
        <AddBillModal
          darkMode={darkMode}
          onClose={() => setShowAddBillModal(false)}
          onAddBill={(data) => {
            handleAddBill(data);
            setShowAddBillModal(false);
          }}
        />
      )}
      
      {/* Edit Bill Modal */}
      {showEditBillModal && currentBill && (
        <EditBillModal
          darkMode={darkMode}
          bill={currentBill}
          onClose={() => {
            setShowEditBillModal(false);
            setCurrentBill(null);
          }}
          onUpdateBill={(data) => {
            handleUpdateBill(currentBill.id, data);
            setShowEditBillModal(false);
            setCurrentBill(null);
          }}
        />
      )}
      
      <ToastContainer position="bottom-right" theme={darkMode ? "dark" : "light"} />
    </div>
  );
}
