import React, { useState } from 'react';
import { FiX } from 'react-icons/fi';
import './Modal.css';

export default function AddExpenseModal({ onClose, onAddExpense }) {
  const [formData, setFormData] = useState({
    title: '',
    amount: '',
    category: 'food',
    date: new Date().toISOString().split('T')[0]
  });
  
  const [errors, setErrors] = useState({});
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
    
    // Clear error for the field being edited
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: undefined
      });
    }
  };
  
  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.title.trim()) {
      newErrors.title = 'Title is required';
    }
    
    if (!formData.amount.trim()) {
      newErrors.amount = 'Amount is required';
    } else if (isNaN(formData.amount) || Number(formData.amount) <= 0) {
      newErrors.amount = 'Amount must be a positive number';
    }
    
    if (!formData.category) {
      newErrors.category = 'Category is required';
    }
    
    if (!formData.date) {
      newErrors.date = 'Date is required';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };
  
  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (validateForm()) {
      onAddExpense({
        ...formData,
        amount: Number(formData.amount)
      });
    }
  };
  
  return (
    <div className="modal-backdrop">
      <div className="modal-container">
        <div className="modal-header">
          <h2>Add Expense</h2>
          <button 
            className="modal-close"
            onClick={onClose}
            aria-label="Close modal"
          >
            <FiX size={20} />
          </button>
        </div>
        
        <form onSubmit={handleSubmit} className="modal-form">
          <div className="form-group">
            <label htmlFor="title">Title</label>
            <input
              type="text"
              id="title"
              name="title"
              value={formData.title}
              onChange={handleChange}
              placeholder="Expense title"
              className={errors.title ? 'input-error' : ''}
            />
            {errors.title && <p className="error-message">{errors.title}</p>}
          </div>
          
          <div className="form-group">
            <label htmlFor="amount">Amount</label>
            <input
              type="text"
              id="amount"
              name="amount"
              value={formData.amount}
              onChange={handleChange}
              placeholder="0.00"
              className={errors.amount ? 'input-error' : ''}
            />
            {errors.amount && <p className="error-message">{errors.amount}</p>}
          </div>
          
          <div className="form-group">
            <label htmlFor="category">Category</label>
            <select
  id="category"
  name="category"
  value={formData.category}
  onChange={handleChange}
  className="form-control"
  style={{
    backgroundColor: "#273449",
    color: "white",
    border: "1px solid #334155",
    borderRadius: "8px",
    padding: "10px 12px",
    width: "100%",
    fontSize: "14px",
    appearance: "none",
    backgroundImage: `url("data:image/svg+xml;charset=UTF-8,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' fill='none' stroke='%2394a3b8' stroke-width='2' stroke-linecap='round' stroke-linejoin='round'%3e%3cpolyline points='6 9 12 15 18 9'%3e%3c/polyline%3e%3c/svg%3e")`,
    backgroundRepeat: "no-repeat",
    backgroundPosition: "right 10px center",
    backgroundSize: "16px",
    paddingRight: "30px"
  }}
  required
>
  <option value="Food" style={{backgroundColor: "#273449", color: "white"}}>Food</option>
  <option value="Entertainment" style={{backgroundColor: "#273449", color: "white"}}>Entertainment</option>
  <option value="Travel" style={{backgroundColor: "#273449", color: "white"}}>Travel</option>
  <option value="Study" style={{backgroundColor: "#273449", color: "white"}}>Study</option>
  <option value="Utilities" style={{backgroundColor: "#273449", color: "white"}}>Utilities</option>
  <option value="Electronics" style={{backgroundColor: "#273449", color: "white"}}>Electronics</option>
  <option value="Other" style={{backgroundColor: "#273449", color: "white"}}>Other</option>
</select>
            {errors.category && <p className="error-message">{errors.category}</p>}
          </div>
          
          <div className="form-group">
            <label htmlFor="date">Date</label>
            <input
              type="date"
              id="date"
              name="date"
              value={formData.date}
              onChange={handleChange}
              className={errors.date ? 'input-error' : ''}
            />
            {errors.date && <p className="error-message">{errors.date}</p>}
          </div>
          
          <div className="modal-footer">
            <button 
              type="button" 
              className="cancel-btn"
              onClick={onClose}
            >
              Cancel
            </button>
            <button 
              type="submit" 
              className="submit-btn"
            >
              Add Expense
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
