// Home.jsx
import React, { useState, useEffect } from 'react';
import { format } from 'date-fns';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { FiRefreshCw, FiPlus, FiTrendingUp, FiEdit2, FiTrash2 } from 'react-icons/fi'; 
import { loadDataFromLocalStorage, saveDataToLocalStorage } from '../lib/storage';
import DashboardHeader from '../components/DashboardHeader';
import WalletCard from '../components/WalletCard';
import ExpenseCard from '../components/ExpenseCard';
import BudgetCard from '../components/BudgetCard';
import TransactionList from '../components/TransactionList';
import TopExpensesChart from '../components/TopExpensesChart';
import AddExpenseModal from '../components/modals/AddExpenseModal';
import AddIncomeModal from '../components/modals/AddIncomeModal';
import EditExpenseModal from '../components/modals/EditExpenseModal';
import './Home.css';

export default function Home({ darkMode, setDarkMode }) {
  const [isLoading, setIsLoading] = useState(true);
  const [wallet, setWallet] = useState({ id: 1, balance: "2000" });
  const [expenses, setExpenses] = useState([]);
  const [showAddExpenseModal, setShowAddExpenseModal] = useState(false);
  const [showAddIncomeModal, setShowAddIncomeModal] = useState(false);
  const [showEditExpenseModal, setShowEditExpenseModal] = useState(false);
  const [currentExpense, setCurrentExpense] = useState(null);
  
  // Budget settings
  const monthlyBudget = 8000;
  const savingsGoal = 15000;

  // Load initial data
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Try to load from localStorage first as a fallback
        const localData = loadDataFromLocalStorage();
        if (localData) {
          setWallet(localData.wallet);
          setExpenses(localData.expenses);
        }
        
        // Then try to fetch from API
        try {
          const walletResponse = await fetch('/api/wallet');
          const expensesResponse = await fetch('/api/expenses');
          
          if (walletResponse.ok && expensesResponse.ok) {
            const walletData = await walletResponse.json();
            const expensesData = await expensesResponse.json();
            
            setWallet(walletData);
            setExpenses(expensesData);
            
            // Save to localStorage for offline use
            saveDataToLocalStorage({ wallet: walletData, expenses: expensesData });
          }
        } catch (apiError) {
          console.error("API error:", apiError);
          // API failed but we already loaded from localStorage if available
        }
      } catch (error) {
        console.error("Error loading data:", error);
        toast.error("Failed to load data");
      } finally {
        setIsLoading(false);
      }
    };
    
    fetchData();
  }, []);

  // Add income handler
  const handleAddIncome = async (amount) => {
    try {
      // First update local state optimistically
      const newBalance = parseFloat(wallet.balance) + amount;
      const updatedWallet = { ...wallet, balance: newBalance.toString() };
      setWallet(updatedWallet);
      
      // Save to localStorage
      saveDataToLocalStorage({ wallet: updatedWallet, expenses });
      
      // Then try API
      try {
        const response = await fetch('/api/wallet/income', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ amount }),
        });
        
        if (response.ok) {
          const serverWallet = await response.json();
          setWallet(serverWallet);
          saveDataToLocalStorage({ wallet: serverWallet, expenses });
        }
      } catch (apiError) {
        console.error("API error when adding income:", apiError);
      }
      
      toast.success("Income added successfully");
    } catch (error) {
      console.error("Error adding income:", error);
      toast.error("Failed to add income");
    }
  };

  // Add expense handler
  const handleAddExpense = async (data) => {
    try {
      // Create a new expense with temporary ID
      const newExpense = {
        id: Date.now(), // temp ID
        ...data,
        amount: parseFloat(data.amount),
        created_at: new Date()
      };
      
      // Update state optimistically
      setExpenses(prev => [newExpense, ...prev]);
      
      // Update wallet balance
      const newBalance = parseFloat(wallet.balance) - parseFloat(data.amount);
      if (newBalance < 0) {
        toast.error("Insufficient funds in wallet");
        setExpenses(prev => prev.filter(e => e.id !== newExpense.id));
        return;
      }
      
      const updatedWallet = { ...wallet, balance: newBalance.toString() };
      setWallet(updatedWallet);
      
      // Save to localStorage
      saveDataToLocalStorage({ 
        wallet: updatedWallet, 
        expenses: [newExpense, ...expenses] 
      });
      
      // Then try API
      try {
        const response = await fetch('/api/expenses', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(data),
        });
        
        if (response.ok) {
          const serverExpense = await response.json();
          
          // Replace temp expense with server one
          setExpenses(prev => 
            prev.map(e => e.id === newExpense.id ? serverExpense : e)
          );
          
          // Get updated wallet from server
          const walletResponse = await fetch('/api/wallet');
          if (walletResponse.ok) {
            const serverWallet = await walletResponse.json();
            setWallet(serverWallet);
            
            // Update localStorage
            saveDataToLocalStorage({ 
              wallet: serverWallet, 
              expenses: expenses.map(e => e.id === newExpense.id ? serverExpense : e)
            });
          }
        } else {
          // Revert changes if API fails
          setExpenses(prev => prev.filter(e => e.id !== newExpense.id));
          setWallet(wallet);
          const errorData = await response.json();
          throw new Error(errorData.message || 'Failed to add expense');
        }
      } catch (apiError) {
        console.error("API error when adding expense:", apiError);
      }
      
      toast.success("Expense added successfully");
    } catch (error) {
      console.error("Error adding expense:", error);
      toast.error(error.message || "Failed to add expense");
    }
  };

  // Edit expense handler
  const handleEditExpense = (expense) => {
    setCurrentExpense(expense);
    setShowEditExpenseModal(true);
  };

  // Update expense handler
  const handleUpdateExpense = async (id, data) => {
    try {
      // Find the expense to update
      const expenseToUpdate = expenses.find(e => e.id === id);
      if (!expenseToUpdate) {
        toast.error("Expense not found");
        return;
      }
      
      // Calculate wallet balance change
      const oldAmount = parseFloat(expenseToUpdate.amount);
      const newAmount = parseFloat(data.amount || oldAmount);
      const amountDiff = oldAmount - newAmount;
      
      // Create updated expense
      const updatedExpense = { ...expenseToUpdate, ...data };
      
      // Update state optimistically
      setExpenses(prev => 
        prev.map(expense => expense.id === id ? updatedExpense : expense)
      );
      
      // Update wallet if amount changed
      if (amountDiff !== 0) {
        const newBalance = parseFloat(wallet.balance) + amountDiff;
        if (newBalance < 0) {
          toast.error("Insufficient funds in wallet");
          setExpenses(prev => 
            prev.map(expense => expense.id === id ? expenseToUpdate : expense)
          );
          return;
        }
        
        const updatedWallet = { ...wallet, balance: newBalance.toString() };
        setWallet(updatedWallet);
        
        // Save to localStorage
        saveDataToLocalStorage({ 
          wallet: updatedWallet, 
          expenses: expenses.map(expense => 
            expense.id === id ? updatedExpense : expense
          ) 
        });
      }
      
      // Then try API
      try {
        const response = await fetch(`/api/expenses/${id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(data),
        });
        
        if (response.ok) {
          const serverExpense = await response.json();
          
          // Update with server data
          setExpenses(prev => 
            prev.map(e => e.id === id ? serverExpense : e)
          );
          
          // Get updated wallet from server
          const walletResponse = await fetch('/api/wallet');
          if (walletResponse.ok) {
            const serverWallet = await walletResponse.json();
            setWallet(serverWallet);
            
            // Update localStorage
            saveDataToLocalStorage({ 
              wallet: serverWallet, 
              expenses: expenses.map(e => e.id === id ? serverExpense : e)
            });
          }
        }
      } catch (apiError) {
        console.error("API error when updating expense:", apiError);
      }
      
      toast.success("Expense updated successfully");
    } catch (error) {
      console.error("Error updating expense:", error);
      toast.error(error.message || "Failed to update expense");
    }
  };

  // Delete expense handler
  const handleDeleteExpense = async (id) => {
    try {
      // Find the expense to delete
      const expenseToDelete = expenses.find(e => e.id === id);
      if (!expenseToDelete) {
        toast.error("Expense not found");
        return;
      }
      
      // Update state optimistically
      setExpenses(prev => prev.filter(expense => expense.id !== id));
      
      // Restore amount to wallet
      const newBalance = parseFloat(wallet.balance) + parseFloat(expenseToDelete.amount);
      const updatedWallet = { ...wallet, balance: newBalance.toString() };
      setWallet(updatedWallet);
      
      // Save to localStorage
      saveDataToLocalStorage({ 
        wallet: updatedWallet, 
        expenses: expenses.filter(expense => expense.id !== id) 
      });
      
      // Then try API
      try {
        const response = await fetch(`/api/expenses/${id}`, {
          method: 'DELETE',
        });
        
        if (response.ok) {
          // Get updated wallet from server
          const walletResponse = await fetch('/api/wallet');
          if (walletResponse.ok) {
            const serverWallet = await walletResponse.json();
            setWallet(serverWallet);
            
            // Update localStorage
            saveDataToLocalStorage({ 
              wallet: serverWallet, 
              expenses: expenses.filter(expense => expense.id !== id)
            });
          }
        }
      } catch (apiError) {
        console.error("API error when deleting expense:", apiError);
      }
      
      toast.success("Expense deleted successfully");
    } catch (error) {
      console.error("Error deleting expense:", error);
      toast.error("Failed to delete expense");
    }
  };

  // Calculate total expenses
  const totalExpenses = expenses.reduce((sum, expense) => sum + parseFloat(expense.amount), 0);

  // Format current date
  const currentDate = format(new Date(), 'EEEE, MMMM d, yyyy â€¢ h:mm a');

  if (isLoading) {
    return (
      <div className="loading-container">
        <p>Loading...</p>
      </div>
    );
  }

  return (
    <div className="app-container">
      {/* App Header with separator */}
      <div className="app-header-separator"></div>
      <DashboardHeader 
        title="Expense Tracker" 
        date={currentDate}
        onAddExpense={() => setShowAddExpenseModal(true)}
        onAddIncome={() => setShowAddIncomeModal(true)}
        darkMode={darkMode}
        setDarkMode={setDarkMode}
      />
      
      {/* Main Dashboard Grid - IMPROVED SPACING */}
      <div className="dashboard-grid">
        {/* Wallet Card */}
        <div className="card wallet-card">
          <div className="card-header">
            <h2 className="card-title">Wallet Balance</h2>
            <button className="btn-icon" aria-label="Refresh">
              <FiRefreshCw size={16} />
            </button>
          </div>
          <div className="card-body">
            <div className="wallet-amount">${parseFloat(wallet.balance).toLocaleString('en-US', {minimumFractionDigits: 2, maximumFractionDigits: 2})}</div>
            
            {/* Sparkline chart */}
            <div className="sparkline">
              {Array.from({ length: 12 }, (_, i) => (
                <div 
                  key={i} 
                  className="sparkline-bar" 
                  style={{ 
                    height: `${Math.floor(Math.random() * 30) + 1}px`,
                    backgroundColor: i === 11 ? '#10b981' : 'rgba(16, 185, 129, 0.5)'
                  }}
                ></div>
              ))}
            </div>
            <div className="sparkline-caption">
              <span><FiTrendingUp size={12} /> 12%</span>
            </div>
            
            <div className="wallet-stats">
              <div className="wallet-stat">
                <div className="stat-label">Available</div>
                <div className="stat-value">${(parseFloat(wallet.balance) - totalExpenses).toLocaleString('en-US', {minimumFractionDigits: 2, maximumFractionDigits: 2})}</div>
                <div className="stat-badge">60%</div>
              </div>
              
              <div className="wallet-stat">
                <div className="stat-label">Spent</div>
                <div className="stat-value">${totalExpenses.toLocaleString('en-US', {minimumFractionDigits: 2, maximumFractionDigits: 2})}</div>
              </div>
            </div>
            
            <div className="savings-goal">
              <div className="savings-header">
                <div className="savings-label">Savings Goal</div>
                <div className="savings-amount">${savingsGoal.toLocaleString('en-US')}</div>
              </div>
              
              <div className="progress-bar">
                <div 
                  className="progress-value" 
                  style={{ 
                    width: `${Math.min(100, (parseFloat(wallet.balance) / savingsGoal) * 100)}%`,
                    backgroundColor: 'var(--primary)'
                  }}
                ></div>
              </div>
              
              <div className="savings-meta">
                <span>{Math.round((parseFloat(wallet.balance) / savingsGoal) * 100)}% completed</span>
                <span>${parseFloat(wallet.balance).toLocaleString('en-US')} / ${savingsGoal.toLocaleString('en-US')}</span>
              </div>
            </div>
            
            <button className="btn btn-success btn-block" onClick={() => setShowAddIncomeModal(true)}>
              <FiRefreshCw size={16} />
              Add Income
            </button>
          </div>
        </div>
        
        {/* Expense Card */}
        <div className="card expense-card">
          <div className="card-header">
            <h2 className="card-title">Expenses</h2>
            <button className="btn-icon" onClick={() => setShowAddExpenseModal(true)} aria-label="Add expense">
              <FiPlus size={16} />
            </button>
          </div>
          <div className="card-body">
            <div className="expense-amount">${totalExpenses.toLocaleString('en-US', {minimumFractionDigits: 2, maximumFractionDigits: 2})}</div>
            
            <div className="progress-bar">
              <div 
                className="progress-value" 
                style={{ 
                  width: `${Math.min(100, (totalExpenses / monthlyBudget) * 100)}%`,
                  backgroundColor: 'var(--danger)'
                }}
              ></div>
            </div>
            
            <div className="expense-stats">
              <div className="expense-stat">
                <div className="stat-label">Top Category</div>
                <div className="stat-value">Travel</div>
              </div>
              
              <div className="expense-stat">
                <div className="stat-label">Avg. Expense</div>
                <div className="stat-value">
                  ${expenses.length ? (totalExpenses / expenses.length).toFixed(2) : '0.00'}
                </div>
              </div>
            </div>
            
            <button className="btn btn-danger btn-block" onClick={() => setShowAddExpenseModal(true)}>
              <FiPlus size={16} />
              Add Expense
            </button>
          </div>
        </div>
        
        {/* Budget Card */}
        <div className="card budget-card">
          <div className="card-header">
            <h2 className="card-title">Budget Overview</h2>
            <div className="budget-percentage">{Math.min(100, Math.round((totalExpenses / monthlyBudget) * 100))}% used</div>
          </div>
          <div className="card-body">
            <div className="budget-chart" style={{ position: 'relative', height: '200px' }}>
              <div style={{ 
                width: '140px', 
                height: '140px', 
                margin: '0 auto', 
                borderRadius: '50%',
                background: `conic-gradient(var(--primary) 0% ${Math.min(100, Math.round((totalExpenses / monthlyBudget) * 100))}%, #e2e8f0 ${Math.min(100, Math.round((totalExpenses / monthlyBudget) * 100))}% 100%)`,
                position: 'relative'
              }}>
                <div style={{
                  position: 'absolute',
                  top: '50%',
                  left: '50%',
                  transform: 'translate(-50%, -50%)',
                  width: '110px',
                  height: '110px',
                  borderRadius: '50%',
                  backgroundColor: 'var(--bg-card)',
                  display: 'flex',
                  flexDirection: 'column',
                  justifyContent: 'center',
                  alignItems: 'center'
                }}>
                  <div style={{color: 'var(--primary)', fontSize: '24px', fontWeight: 'bold'}}>
                    {Math.min(100, Math.round((totalExpenses / monthlyBudget) * 100))}%
                  </div>
                  <div style={{color: 'var(--text-secondary)', fontSize: '12px'}}>of budget</div>
                </div>
              </div>
            </div>
            
            <div className="budget-stats">
              <div className="budget-stat">
                <div className="stat-label">Monthly Budget</div>
                <div className="stat-value">${monthlyBudget.toLocaleString('en-US')}</div>
              </div>
              
              <div className="budget-stat">
                <div className="stat-label">Spent so far</div>
                <div className="stat-value">${totalExpenses.toLocaleString('en-US')}</div>
              </div>
            </div>
            
            <div style={{ textAlign: 'center', margin: '15px 0' }}>
              <div style={{ color: 'var(--success)' }}>You're under budget. Good job!</div>
              <div style={{ fontWeight: 'bold', fontSize: '16px' }}>
                ${(monthlyBudget - totalExpenses).toLocaleString('en-US')} remaining
              </div>
            </div>
            
            <div className="budget-actions">
              <button className="btn btn-outline">Reports</button>
              <button className="btn btn-outline">Budgets</button>
              <button className="btn btn-outline">Bills</button>
            </div>
          </div>
        </div>
      </div>

      {/* Transactions and Top Expenses - IMPROVED SPACING */}
      <div className="transactions-grid">
        <div className="card">
          <div className="card-header">
            <h2 className="card-title">Recent Transactions</h2>
          </div>
          <div className="card-body">
            {expenses.length > 0 ? (
              <div className="transaction-list">
                {expenses.slice(0, 5).map((expense) => (
                  <div key={expense.id} className="transaction-item">
                    <div className="transaction-info">
                      <div className="transaction-title">{expense.title}</div>
                      <div className="transaction-date">{new Date(expense.date).toLocaleDateString()}</div>
                    </div>
                    <div className="transaction-amount">${parseFloat(expense.amount).toFixed(2)}</div>
                    <div className="transaction-actions">
                      <button 
                        className="btn-icon" 
                        onClick={() => handleEditExpense(expense)}
                        aria-label="Edit"
                      >
                        <FiEdit2 size={14} />
                      </button>
                      <button 
                        className="btn-icon" 
                        onClick={() => handleDeleteExpense(expense.id)}
                        aria-label="Delete"
                      >
                        <FiTrash2 size={14} />
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="empty-state">
                <p>No transactions yet.</p>
                <div 
                  className="empty-state-action"
                  onClick={() => setShowAddExpenseModal(true)}
                >
                  Add your first expense
                </div>
              </div>
            )}
          </div>
        </div>
        
        <div className="card">
          <div className="card-header">
            <h2 className="card-title">Top Expenses</h2>
          </div>
          <div className="card-body">
            {expenses.length > 0 ? (
              <div className="top-expenses-container">
                {/* Visualize top expenses by category here */}
                <TopExpensesChart expenses={expenses} />
              </div>
            ) : (
              <div className="empty-state">
                <p>No expense data available</p>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Modals */}
      {showAddExpenseModal && (
        <AddExpenseModal
          onClose={() => setShowAddExpenseModal(false)}
          onAddExpense={(data) => {
            handleAddExpense(data);
            setShowAddExpenseModal(false);
          }}
        />
      )}

      {showAddIncomeModal && (
        <AddIncomeModal
          onClose={() => setShowAddIncomeModal(false)}
          onAddIncome={(amount) => {
            handleAddIncome(amount);
            setShowAddIncomeModal(false);
          }}
        />
      )}

      {showEditExpenseModal && currentExpense && (
        <EditExpenseModal
          expense={currentExpense}
          onClose={() => {
            setShowEditExpenseModal(false);
            setCurrentExpense(null);
          }}
          onUpdateExpense={(data) => {
            handleUpdateExpense(currentExpense.id, data);
            setShowEditExpenseModal(false);
            setCurrentExpense(null);
          }}
        />
      )}

      <ToastContainer position="bottom-right" theme={darkMode ? "dark" : "light"} />
    </div>
  );
}
